import React from "react";
import SearchBar from "../SearchBar/SearchBar";
import CardItem from "../CardItem/CardItem";
import url from "../../utilities/url";
import useFetch from "../../utilities/useFetch";
import Loading from "../Loading/Loading";
import Error from "../Error/Error";

const Category = ({ categoryType }) => {
    const searchString = "Muse";
    const urlString = url.search + searchString + "&type=" + categoryType;

    const [status, error, fetchedData] = useFetch(urlString, categoryType);

    console.log(fetchedData);
    console.log(urlString);

    const renderedCards = fetchedData.map((data) => (
        <CardItem
            img={data.images[0].url}
            imgUrl={data.external_urls.spotify}
            header={data.name}
            headerUrl={data.external_urls.spotify}
            footer={data.followers.total}
        />
    ));

    return (
        <div className="content">
            <SearchBar name={categoryType} />

            {status === "error" && <Error error={error} />}
            {status === "loading" && <Loading />}
            {status === "loaded" && (
                <div>
                    <div className="ui stackable three column grid">
                        {renderedCards}
                    </div>
                </div>
            )}
        </div>
    );
};

export default Category;
